var squel = require('./squel');

function intersectsPointQuery(geometryField, latitude, longitude) {
    return "st_intersects({0}, st_setsrid(st_point({1}, {2}), 4326))".format(geometryField, longitude, latitude);
}

if (!String.prototype.format) {
    String.prototype.format = function () {
        var args = arguments;
        return this.replace(/{(\d+)}/g, function (match, number) {
            return typeof args[number] != 'undefined'
                ? args[number]
                : match
                ;
        });
    };
}

var str = squel.select({ autoQuoteAliasNames: false, separator: "\n" })
.from("DATES","d")
// .where('l.name != \'parcels\'')
.where('st_intersects(geometry, st_setsrid(st_point(23.11, 234.233), 4326))')
.toString();


